import styled from 'styled-components';

export const TextProgress = styled.div`
  font-family: Verdana;
  color: white;
  text-transform: uppercase;
  font-size: 5em;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%,-50%); 
  &:before {
    content: attr(progress-text);
    position: absolute;
    top: 0;
    left: 0;
    color: #000;
    width: ${({progress}) => progress};
    overflow: hidden;
    transition: 1s;
  }
`;
