import styled from 'styled-components';
import { BOX_HEIGHT, BOX_WIDTH } from "./Growth";

export const TextProgress = styled.div`
  font-family: GillSans-UltraBold;
  text-transform: uppercase;
  font-size: 5em;
  transform: scale(1.5,1.5);
  text-align: left;
  cursor: default;

  -webkit-text-stroke: ${({color}) => `.5px ${color}`};
  background: ${({progress, color}) => `-webkit-linear-gradient(0,${color} ${progress},#282c34 ${progress},#282c34 100%)`};
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
`;

export const JumpingNotification = styled.div`
  position: absolute;
  top: 100px;
  left: 300px;
  width: ${BOX_WIDTH}px;
  height: ${BOX_HEIGHT}px;
  background: white;
  z-index: 1;
  border-radius: 5px;
  box-shadow: 2px 2px 20px 5px #0000006b;
  cursor: pointer;
`;
