import styled from 'styled-components';

export const TextProgress = styled.div`
  font-family: Verdana;
  text-transform: uppercase;
  font-size: 5em;
  color: #262626;
  transform: scale(1.5,1.5);
  text-align: left;

  -webkit-text-stroke: ${({color}) => `2px ${color}`};
  background: ${({progress, color}) => `-webkit-linear-gradient(0,${color} ${progress},#282c34 ${progress},#262626 100%)`};
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
`;
