import styled from 'styled-components';

export const TextProgress = styled.div`
  font-family: GillSans-UltraBold;
  text-transform: uppercase;
  font-size: 5em;
  transform: scale(1.5,1.5);
  text-align: left;
  cursor: default;

  -webkit-text-stroke: ${({color}) => `.5px ${color}`};
  background: ${({progress, color}) => `-webkit-linear-gradient(0,${color} ${progress},#282c34 ${progress},#282c34 100%)`};
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
`;

export const JumpingNotification = styled.div`
  position: absolute;
  top: ${({top}) => top}px;
  left: ${({left}) => left}px;
  width: ${({boxWidth}) => boxWidth}px;
  height: ${({boxHeight}) => boxHeight}px;
  background: white;
  z-index: 1;
  border-radius: 5px;
  box-shadow: 2px 2px 20px 5px #0000006b;
  cursor: pointer;
`;

export const StartButton = styled.button`
  position: absolute;
  top: 50%;
  left: 50%;
  z-index: 1;
  border: none;
  box-shadow: 2px 2px 20px 5px #0000006b;
  border-radius: 5px;  
  font-size: 2em;
  padding: 10px 40px;
  font-family: GillSans-bold;
  cursor: pointer;
  background: #e9bb5f;
  color: #282c34;
`;
