import React, { useState } from 'react';
import { Swipe } from 'react-swipe-component'

import { NotificationBox } from './style';
import { BOX_HEIGHT, BOX_WIDTH } from '../consts';
import { useInterval } from '../utilis';

const availSpaceY = window.innerHeight - BOX_HEIGHT;
const availSpaceX = window.innerWidth - BOX_WIDTH;
const getX = () => Math.round(Math.random() * availSpaceY);
const getY = () => Math.round(Math.random() * availSpaceX);

export const Notifications = () => {
  const [delay, setDelay] = useState(2000);
  const [notification, setNotification] = useState();
  const [locations, setLocations] = useState([{ x:getX(), y:getY() }]);

  // Increment the counter.
  useInterval(() => {
    setLocations([
      ...locations,
      {
        x: getX(),
        y: getY()
      }
    ]);

  }, delay);

  // Make it faster every second!
  useInterval(() => {
    if (delay > 10) {
      setDelay(delay / 1.5);
    }
  }, 10000);

  const handleReset = () => {
    setDelay(1000);
  };

  return (
    <>
      {
        locations.map((location, idx) => (
          <Swipe key={idx} onSwipedLeft={() => console.log('swipe left')} onSwipedRight={() => console.log('swipe right')}>
            <NotificationBox top={location.x} left={location.y}>
              {locations.length}
            </NotificationBox>
          </Swipe>
        ))
      }
    </>
  );
}
