import React, { useState } from 'react';
import { TextProgress, JumpingBox, StartButton } from './style';

export const BOX_HEIGHT = 50;
export const BOX_WIDTH = 200;

const availSpaceY = window.innerHeight - BOX_HEIGHT;
const availSpaceX = window.innerWidth - BOX_WIDTH;

export const Main = () => {
  const [top, setTop] = useState();
  const [left, setLeft] = useState();
  const [start, setStart] = useState(false);

  const [growthProgress, setGrowthProgress] = useState(0);
  const [esteemProgress, setEsteemProgress] = useState(0);
  const [belongProgress, setBelongProgress] = useState(0);
  const [safetyProgress, setSafetyProgress] = useState(0);
  const [energyProgress, setEnergyProgress] = useState(0);

  const growth = {
    color: '#e59c99',
    title: 'growth',
    tools: ['good','good','good','good','good','bad','bad','bad','bad','bad'],
    setProgress: setGrowthProgress,
    progress: growthProgress
  };

  const esteem = {
    color: '#e9bb5f',
    title: 'esteem',
    tools: ['good','good','good','good','good','bad','bad','bad','bad','bad'],
    setProgress: setEsteemProgress,
    progress: esteemProgress
  };

  const belong = {
    color: '#cc6262',
    title: 'belong',
    tools: ['good','good','good','good','good','bad','bad','bad','bad','bad'],
    setProgress: setBelongProgress,
    progress: belongProgress
  };

  const safety = {
    color: '#e59c99',
    title: 'safety',
    tools: ['good','good','good','good','good','bad','bad','bad','bad','bad'],
    setProgress: setSafetyProgress,
    progress: safetyProgress
  };

  const energy = {
    color: '#a8e4e4',
    title: 'energy',
    tools: ['good','good','good','good','good','bad','bad','bad','bad','bad'],
    setProgress: setEnergyProgress,
    progress: energyProgress
  };

  const skils = [growth,esteem,belong,safety,energy]
  const startGame = () => {
    setStart(true);
    setInterval(moveBox, 1500);
  };

  const moveBox = () => {
    const randNumTop = Math.round(Math.random() * availSpaceY);
    const randNumLeft = Math.round(Math.random() * availSpaceX);

    setTop(randNumTop);
    setLeft(randNumLeft);

  };

  const handleClick = () => {

  }

  return (
    <div>
      {!start ? <StartButton onClick={startGame}>START</StartButton> :
        <JumpingBox top={top} left={left} boxWidth={BOX_WIDTH} boxHeight={BOX_HEIGHT} onClick={handleClick}/>}
      <TextProgress color='#e59c99' progress={growthProgress}>growth</TextProgress>
    </div>
  );
}
