import styled from 'styled-components';
import { BOX_HEIGHT, BOX_WIDTH } from './consts';

const roundBorder = `
  box-shadow: 2px 2px 20px 5px #0000006b;
  border-radius: 5px; 
`;

export const TextProgress = styled.div`
  font-family: GillSans-UltraBold;
  text-transform: uppercase;
  font-size: 14em;
  line-height: .85em;
  text-align: left;
  cursor: default;
  user-select: none;
  -webkit-text-stroke: ${({color}) => `1px ${color}`};
  background: ${({progress, color}) => `-webkit-linear-gradient(0,${color} ${progress}%,#282c34 ${progress}%,#282c34 100%)`};
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
`;

export const JumpingBox = styled.div`
  position: absolute;
  top: ${({top}) => top}px;
  left: ${({left}) => left}px;
  width: ${BOX_WIDTH}px;
  height: ${BOX_HEIGHT}px;
  background: white;
  z-index: 1;
  cursor: pointer;
  ${roundBorder}
`;

export const StartButton = styled.button`
  position: absolute;
  z-index: 1;
  border: none;
  font-size: 2em;
  padding: 10px 40px;
  font-family: GillSans-bold;
  cursor: pointer;
  background: #e9bb5f;
  color: #282c34;
  outline: none;
  margin: 430px -200px;
  ${roundBorder}
`;
