import React, { useState } from 'react';
import { Swipe } from 'react-swipe-component'

import { ReactComponent as Logo } from '../../belongAssets/loveBad1.svg'

import { NotificationBox } from './style';
import { BOX_HEIGHT, BOX_WIDTH, skills } from '../consts';
import { useInterval } from '../utilis';

export const Notifications = () => {
  //const
  const availSpaceY = window.innerHeight - BOX_HEIGHT;
  const availSpaceX = window.innerWidth - BOX_WIDTH;
  const getX = () => Math.round(Math.random() * availSpaceY);
  const getY = () => Math.round(Math.random() * availSpaceX);
  const getRandomInt = max => Math.floor(Math.random() * Math.floor(max));


  //Hooks state
  const [delay, setDelay] = useState(2000);
  const [notification, setNotification] = useState([...Array(48).keys()]);
  const [locations, setLocations] = useState([{ x:getX(), y:getY() }]);
  const [currNotification, setCurrNotification] = useState([{0:'4',1:'9'}]);

  useInterval(() => {
    setLocations([
      ...locations,
      {
        x: getX(),
        y: getY()
      }
    ]);
    const notificationNumber = notification[getRandomInt(notification.length)].toString();
    if (notificationNumber.length === 1) setCurrNotification([
      ...currNotification,
      {
        0: '0',
        1: notificationNumber
      }
      ]);
    else setCurrNotification([
      ...currNotification,
      {
        0: notificationNumber[0],
        1: notificationNumber[1]
      }
      ]);
    console.log(notification.length)
    if (!notification.length) {
      console.log('game over')
      clearInterval(useInterval)
    }
    setNotification(notification.filter((_, idx) => idx !== +notificationNumber));
  }, delay);

  // Make it faster every second!
  useInterval(() => {
    if (delay > 10) {
      setDelay(delay / 1.5);
    }
  }, 10000);

  const handleReset = () => {
    setDelay(1000);
  };

  return (
    <>
      {
        locations.map((location, idx) => (
          <Swipe key={idx} onSwipedLeft={() => console.log('swipe left')} onSwipedRight={() => console.log('swipe right')}>
            <NotificationBox top={location.x} left={location.y}>
              {currNotification[idx] ? skills[currNotification[idx][0]].tools[currNotification[idx][1]] : null}
              <Logo />
            </NotificationBox>
          </Swipe>
        ))
      }
    </>
  );
};
