import React, { useState } from 'react';
import { TextProgress, JumpingNotification } from './style';

export const BOX_HEIGHT = 50;
export const BOX_WIDTH = 200;

const availSpaceY = window.innerHeight - BOX_HEIGHT;
const availSpaceX = window.innerWidth - BOX_WIDTH;

export const Growth = () => {
  const [top, setTop] = useState(0);
  const [left, setLeft] = useState(0);

  const startGame = () => {
    setInterval(moveBox, 3000);
  };

  const moveBox = () => {
    const randNumTop = Math.round(Math.random() * availSpaceY);
    const randNumLeft = Math.round(Math.random() * availSpaceX);

    setTop(randNumTop);
    setLeft(randNumLeft);
  };

  return (
    <div>
      <JumpingNotification top={top} left={left} boxWidth={BOX_WIDTH} boxHeight={BOX_HEIGHT}/>
      <button onClick={startGame}>START</button>
      <TextProgress color='#e59c99' progress='60%'>growth</TextProgress>
      <TextProgress color='#e9bb5f' progress='20%'>esteem</TextProgress>
      <TextProgress color='#cc6262' progress='40%'>belong</TextProgress>
      <TextProgress color='#6c7cc9' progress='50%'>safety</TextProgress>
      <TextProgress color='#a8e4e4' progress='50%'>energy</TextProgress>
    </div>
  );
}
