import React, { useState } from 'react';
import { Swipe } from 'react-swipe-component'

import { BOX_HEIGHT, BOX_WIDTH, skills} from './consts';
import { TextProgress, StartButton } from './style';
import { JumpingBox, exitPoints } from './JumpingBox/style';

const availSpaceY = window.innerHeight - BOX_HEIGHT;
const availSpaceX = window.innerWidth - BOX_WIDTH;

export const Main = () => {
  const [top, setTop] = useState();
  const [left, setLeft] = useState();
  const [start, setStart] = useState(false);
  const [skillNToll, setSkillNToll] = useState([]);

  const startGame = () => {
    setInterval(moveBox, 2000);
  };

  const getRandomInt = max => Math.floor(Math.random() * Math.floor(max));

  const moveBox = () => {
    const randNumTop = Math.round(Math.random() * availSpaceY);
    const randNumLeft = Math.round(Math.random() * availSpaceX);

    setTop(randNumTop);
    setLeft(randNumLeft);

    console.log({randNumTop,randNumLeft})
    let skillNo;

    skillNo = getRandomInt(5);
    const tools = skills[skillNo].tools;
    const randToolNo = getRandomInt(tools.length);
    const randomTool = tools[randToolNo];
    setSkillNToll({ skillNo, tool:randomTool });
    tools.splice(randToolNo, 1);
    if (!start) setStart(true);
  };

  return (
    <div>
      {!start ? <StartButton onClick={startGame}>START GAME</StartButton> :
        <Swipe onSwipedLeft={() => console.log('swipe left')} onSwipedRight={() => console.log('swipe right')}>
          <JumpingBox location={exitPoints.topLeft} top={top} left={left}>
            {skillNToll.tool}
          </JumpingBox>
        </Swipe>}
      {skills.map((skill) => <TextProgress key={skill.title} color={skill.color} progress={skill.progress}>{skill.title}</TextProgress>)}
    </div>
  );
};
