import React from 'react';
import { TextProgress, JumpingNotification } from './style';


window.onLoad = startGame();

const startGame = () => {
  const windowHeight = window.innerHeight;
  const windowWidth = window.innerWidth;


  const availSpaceY = windowHeight - imageHeight;
  const availSpaceX = windowWidth - imageWidth;

  const changeInterval = 3000; // Time has to be in miliseconds. So, 3000 is 3 seconds
  setInterval(moveImage, changeInterval);
}

function moveImage(){
  var randNum_V = Math.round(Math.random() * availSpaceY);
  var randNum_H = Math.round(Math.random() * availSpaceX);

  imageElement.style.top = randNum_V + "px";
  imageElement.style.left = randNum_H + "px";
}


export const Growth = () => (
  <div>
    <JumpingNotification />
    <TextProgress color='#e59c99' progress='60%'>growth</TextProgress>
    <TextProgress color='#e9bb5f' progress='20%'>esteem</TextProgress>
    <TextProgress color='#cc6262' progress='40%'>belong</TextProgress>
    <TextProgress color='#6c7cc9' progress='50%'>safety</TextProgress>
    <TextProgress color='#a8e4e4' progress='50%'>energy</TextProgress>
  </div>
);
